import { useEffect, useState } from "react";
import Header from "./Header";
import Login from "./Login";

const App = ({children}) => {
  const [loginAcccunt, setLoginAccount] = useState();

  useEffect(() => {
    setLoginAccount(sessionStorage.getItem("login"));
  }, [])

  return (
    <div className="dark">
      if(!loginAcccunt) {
        <Login />
      }


        <Header />
        {children}
    </div>
  );
};

export default App;
